import React, { useContext, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import ContextRecipes from '../context/ContextRecipes';
import Footer from '../components/Footer';
import Header from '../components/Header';
import { requestDrinkBySelectedFilter, requestDrinkFilters,
  requestDrinks } from '../service/RequestAPI';
import Recipes from './Recipes';
import allDrinks from '../images/AllDrinks.png';
import cocaDrinks from '../images/cocaDrink.png';
import cocktailDrinks from '../images/cocktailDrink.png';
import shakeDrinks from '../images/shakeDrink.png';
import ordinaryDrinks from '../images/ordinaryDrink.png';
import othersDrinks from '../images/othersDrinks.png';

const filtersIcons = [ordinaryDrinks, cocktailDrinks, shakeDrinks,
  othersDrinks, cocaDrinks];

export default function Drinks() {
  const { setTitle, setRecipesData,
    requestDrink, setFilters, filters, setRequestDrink,
    setFilterToggle, filterToggle } = useContext(ContextRecipes);
  const TWELVE = 12;
  const FIVE = 5;

  const submitFilter = ({ target }) => {
    if (target.className === '') {
      target.className = 'click';
      requestDrinkBySelectedFilter(target.value)
        .then((drink) => setRequestDrink(drink.drinks));
      setFilterToggle(false);
    } else {
      target.className = '';
      requestDrinkBySelectedFilter(target.value)
        .then((drink) => setRequestDrink(drink.drinks));
      setFilterToggle(true);
    }
  };

  useEffect(() => {
    setTitle('Drinks');
    requestDrinkFilters().then((filter) => setFilters(filter.drinks));
    requestDrinks().then((drink) => {
      setRecipesData(drink.drinks);
    });
  }, [setTitle, setRecipesData, setFilters]);

  return (
    <div>
      <Header />
      <div>
        <div className="div_filters">
          <button
            type="button"
            data-testid="All-category-filter"
            onClick={ () => {
              setRequestDrink([]);
            } }
          >
            <img src={ allDrinks } alt="All drinks icon" />

          </button>
          {
            filters.length > 0
            && filters.slice(0, FIVE).map((category, index) => (
              <button
                key={ category.strCategory }
                value={ category.strCategory }
                type="button"
                data-testid={ `${category.strCategory}-category-filter` }
                onClick={ submitFilter }
                className="btn_filters"
              >
                <img src={ filtersIcons[index] } alt="filter drinks icon" />
              </button>))
          }

        </div>
        {(requestDrink.length > 1 && !filterToggle)
          ? requestDrink.slice(0, TWELVE).map((drink, index) => (
            <div key={ drink.idDrink } data-testid={ `${index}-recipe-card` }>
              <NavLink to="/drinks">
                <img
                  src={ drink.strDrinkThumb }
                  alt="imagem do drink"
                  data-testid={ `${index}-card-img` }
                  style={ { maxWidth: '600px' } }
                />
                <p data-testid={ `${index}-card-name` }>{drink.strDrink}</p>
              </NavLink>
            </div>
          )) : <Recipes />}
      </div>
      <Footer />
    </div>
  );
}
