import React from 'react';
import PropTypes from 'prop-types';
import '../style/IngredientsInProgress.css';
import { useLocation } from 'react-router-dom';
import { getLocalStorage,
  saveRecipeInProgressLocalStorage } from '../service/LocalStorage';

export default function IngredientProgress(props) {
  const { recipeType } = props;
  const location = useLocation();
  const adressType = location.pathname.split('/')[1];
  console.log(recipeType);
  const handleCheckbox = ({ target }) => {
    if (target.checked && adressType === 'meals') {
      target.parentElement.className = 'radio-ckecked';
      const inProgressRecipes = getLocalStorage('inProgressRecipes');
      // if (id !== null) {
      //   console.log('teste');
      if (!inProgressRecipes.meals) {
        inProgressRecipes.meals = {};
      }

      if (inProgressRecipes.meals[recipeType.idMeal]) {
        inProgressRecipes.meals[recipeType.idMeal].push(+target.name);
      } else {
        inProgressRecipes.meals[recipeType.idMeal] = [+target.name];
      }
      // const recipe = { ...inProgressRecipes, meals: { ...inProgressRecipes.meals, recipeType[idMeal] : [target.key]  } };
      saveRecipeInProgressLocalStorage(inProgressRecipes);
    }
    if (!target.checked) {
      target.parentElement.className = '';
    }
  };

  function renderIngredients(param1) {
    const asArray = Object.entries(recipeType);
    const filtered = asArray.filter(([key, value]) => key.includes(param1)
    && value !== null && value !== '');
    const ingredients = filtered.map((el) => el[1]);
    return ingredients;
  }

  const ingredientsName = renderIngredients('Ingredient');
  const IngredientsQuantity = renderIngredients('Measure');

  const juntaArrays = () => {
    const newArray = [];
    for (let index = 0; index < ingredientsName.length; index += 1) {
      newArray.push(`${IngredientsQuantity[index]} ${ingredientsName[index]}`);
    }
    return newArray;
  };
  return (
    <div>
      {juntaArrays().map((ingred, index) => (
        <label
          key={ ingred }
          data-testid={ `${index}-ingredient-step` }
          htmlFor={ ingred }
        >
          {ingred}
          {' '}
          <input
            type="checkbox"
            name={ index }
            id={ ingred }
            onChange={ handleCheckbox }
          />
          {' '}

        </label>))}

    </div>
  );
}

IngredientProgress.propTypes = {
  recipeType: PropTypes.shape({
  }).isRequired,
};
